// components/RecentlyPlayed/RecentlyPlayed.js

import React, { useState, useEffect } from 'react';
import spotifyApi from '../spotifyAuth';
import PropTypes from 'prop-types';

function RecentlyPlayed({ setFavorites }) {
  const [recentlyPlayed, setRecentlyPlayed] = useState([]);
  const [showHeadline, setShowHeadline] = useState(false);
  const isTrackInFavorites = favorites.some((fav) => fav.id === track.track.id);

  useEffect(() => {
    // Fetch the user's recently played tracks
    spotifyApi.getMyRecentlyPlayedTracks({ limit: 50 }).then((response) => {
      setRecentlyPlayed(response.items);
      setShowHeadline(true); // Set to true after fetching data
    });
  }, []);

  const handleRemoveFromFavorites = (track) => {
    setFavorites((prevFavorites) => prevFavorites.filter((fav) => fav.id !== track.id));
  };

  return (
    <div>
      {showHeadline && <h2>Recently Played</h2>}
      <ul className="recently-played-list">
        {recentlyPlayed.map((track) => (
          <li key={track.track.id} className="recently-played-item">
            <img
              src={track.track.album.images[2].url} // Assuming you want to display the album art
              alt={track.track.name}
              className="album-art"
            />
            <div className="track-info">
              <p className="track-name">{track.track.name}</p>
              <p className="artists">{track.track.artists.map((artist) => artist.name).join(', ')}</p>
              <button
                  onClick={() => (isTrackInFavorites ? handleRemoveFromFavorites(track.track) : handleAddToFavorites(track.track))}
                  className={isTrackInFavorites ? "remove-from-favorites-button" : "add-to-favorites-button"}
              >
                  {isTrackInFavorites ? "Remove from Favorites" : "Add to Favorites"}
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

RecentlyPlayed.propTypes = {
  setFavorites: PropTypes.func.isRequired,
};

export default RecentlyPlayed;
