// components/Search.js
import React, { useState } from 'react';
import spotifyApi from '../spotifyAuth';

function Search() {
  const [query, setQuery] = useState('');
  const [searchResults, setSearchResults] = useState(null);

  const handleSearch = async () => {
    try {
      // Use the Spotify Web API to search for tracks, artists, albums, and playlists
      const response = await spotifyApi.search(query, /*['track', 'artist', 'album', 'playlist'],*/ { limit: 5 });

      // Update state with the search results
      setSearchResults(response);
    } catch (error) {
      console.error('Error performing search:', error);
    }
  };

  return (
    <div>
      <h2>Search</h2>
      <div>
        <input
          type="text"
          placeholder="Enter search query"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {searchResults && (
        <div>
          <h3>Search Results</h3>
          {/* Display the search results based on the response structure */}
          {/* Adjust the rendering logic based on the actual structure of the response */}
          {searchResults.tracks && (
            <div>
              <h4>Tracks</h4>
              <ul>
                {searchResults.tracks.items.map((track) => (
                <li key={track.id} className="recently-played-item">
                    <img
                    src={track.album.images[2].url}
                    alt={track.name}
                    className="album-art"
                    />
                    <div className="track-info">
                    <p className="track-name">{track.name}</p>
                    <p className="artists">
                        {track.artists.map((artist) => artist.name).join(', ')}
                    </p> 
                    </div>
                </li>
                ))}
              </ul>
            </div>
          )}
          {searchResults.artists && (
            <div>
              <h4>Artists</h4>
              <ul>
                {searchResults.artists.items.map((artist) => (
                  <li key={artist.id} className="top-artists-item">
                  {artist.images.length > 0 && (
                        <img
                            src={artist.images[2].url} // Assuming you want to display the artist image
                            alt={artist.name}
                            className="artist-image"
                        />
                    )}
                    <div className="artist-info">
                    <p className="artist-name">{artist.name}</p>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          )}
          {searchResults.albums && (
            <div>
              <h4>Albums</h4>
              <ul>
                {searchResults.albums.items.map((album) => (
                  <li key={album.id} className="recently-played-item">{album.name}</li>
                ))}
              </ul>
            </div>
          )}
          {searchResults.playlists && (
            <div>
              <h4>Playlists</h4>
              <ul>
                {searchResults.playlists.items.map((playlist) => (
                  <li key={playlist.id} className="recently-played-item">{playlist.name}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Search;
