// components/RecentlyPlayed/RecentlyPlayed.js

import React, { useState, useEffect } from 'react';
import spotifyApi from '../spotifyAuth';

function RecentlyPlayed() {
  const [recentlyPlayed, setRecentlyPlayed] = useState([]);
  const [showHeadline, setShowHeadline] = useState(false);

  useEffect(() => {
    // Fetch the user's recently played tracks
    spotifyApi.getMyRecentlyPlayedTracks({ limit: 50 }).then((response) => {
      setRecentlyPlayed(response.items);
      setShowHeadline(true); // Set to true after fetching data
    });
  }, []);

  const markAsFavorite = (track) => {
    // Check if the track is already marked as a favorite
    const isFavorite = favorites.some((fav) => fav.id === track.track.id);

    if (!isFavorite) {
      // Add the track to the favorites list
      setFavorites((prevFavorites) => [...prevFavorites, track.track]);
    } else {
      // Remove the track from the favorites list
      setFavorites((prevFavorites) => prevFavorites.filter((fav) => fav.id !== track.track.id));
    }
  };

  const createPlaylist = () => {
    // Ensure there are favorite songs to create a playlist
    if (favorites.length === 0) {
      alert('Please mark some songs as favorites before creating a playlist.');
      return;
    }

    // Get the user's Spotify ID
    spotifyApi.getMe().then((userData) => {
      const userId = userData.id;

      // Create a new playlist
      spotifyApi.createPlaylist(userId, {
        name: 'My Favorite Songs',
        public: false, // Set to true if you want the playlist to be public
      }).then((playlistData) => {
        const playlistId = playlistData.id;

        // Add the selected favorite songs to the playlist
        const trackUris = favorites.map((fav) => fav.uri);
        spotifyApi.addTracksToPlaylist(userId, playlistId, trackUris).then(() => {
          alert('Playlist created successfully!');
        });
      });
    });
  };

  return (
    <div>
      {showHeadline && <h2>Recently Played</h2>}
      <ul className="recently-played-list">
        {recentlyPlayed.map((track) => (
          <li key={track.track.id} className="recently-played-item">
            <button onClick={() => markAsFavorite(track)}>
              {favorites.some((fav) => fav.id === track.track.id) ? 'V' : ''}
            </button>
            <img
              src={track.track.album.images[2].url} // Assuming you want to display the album art
              alt={track.track.name}
              className="album-art"
            />
            <div className="track-info">
              <p className="track-name">{track.track.name}</p>
              <p className="artists">{track.track.artists.map((artist) => artist.name).join(', ')}</p>
            </div>
          </li>
        ))}
      </ul>
      {/* Display the "Create Playlist" button */}
      <button onClick={createPlaylist}>Create Playlist</button>
      {/* Display the favorites list */}
      <div>
        <h2>Favorites</h2>
        <ul>
          {favorites.map((fav) => (
            <li key={fav.id}>{fav.name}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default RecentlyPlayed;
