// components/Slideshow.js

import React, { useState, useEffect } from 'react';

const Slideshow = ({ username, topTracks, topArtists, topGenres }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentSlide((prevSlide) => (prevSlide + 1) % 5); // 5 slides in total
    }, 5000); // 5000 milliseconds (5 seconds) per slide

    // Clean up the interval when the component unmounts
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="slideshow-container">
      {currentSlide === 0 && (
        <div className="slide">
          <h2>Hello {username}!</h2>
          <p>Welcome to your Monthly Wrapped!</p>
        </div>
      )}
      {currentSlide === 1 && (
        <div className="slide">
          <h2>Your Top 5 Tracks</h2>
          <ul>
            {topTracks.map((track, index) => (
              <li key={index}>{track.name} - {track.artists.map((artist) => artist.name).join(', ')}</li>
            ))}
          </ul>
        </div>
      )}
      {currentSlide === 2 && (
        <div className="slide">
          <h2>Your Top 5 Artists</h2>
          <ul>
            {topArtists.map((artist, index) => (
              <li key={index}>{artist.name}</li>
            ))}
          </ul>
        </div>
      )}
      {currentSlide === 3 && (
        <div className="slide">
          <h2>Your Top 5 Genres</h2>
          <ul>
            {topGenres.map((genre, index) => (
              <li key={index}>{genre}</li>
            ))}
          </ul>
        </div>
      )}
      {currentSlide === 4 && (
        <div className="slide">
          <h2>Thank You!</h2>
        </div>
      )}
    </div>
  );
};

export default Slideshow;
