// components/Search.js

import React, { useState, useEffect } from 'react';
import spotifyApi from '../spotifyAuth';

function Search({setFavorites}) {
  const [query, setQuery] = useState('');
  const [searchTypeShow, setSearchTypeShow] = useState('track');
  const [searchTypeNew, setSearchTypeNew] = useState('tracks');
  const [searchResults, setSearchResults] = useState(null);
  const [favorites, setFavoritesState] = useState([]);

  useEffect(() => {
    // Load favorites from localStorage on component mount
    const storedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavoritesState(storedFavorites);
    setFavorites(storedFavorites);
  }, [setFavorites]);

  const handleSearch = async () => {
    try {
      const response = await spotifyApi.search(query, [searchTypeShow], { limit: 50 });

      // Use the Spotify Web API to search based on the selected type
      let newSearchType = '';
      if (searchTypeShow === 'track') {
        newSearchType = 'tracks';
      } else if (searchTypeShow === 'artist') {
        newSearchType = 'artists';
      } else if (searchTypeShow === 'playlist') {
        newSearchType = 'playlists';
      } else if (searchTypeShow === 'album') {
        newSearchType = 'albums';
      }

      // Extract and update state with the top results
      if (response[newSearchType] && response[newSearchType]['items']) {
        setSearchResults(response[newSearchType]['items']);
        setSearchTypeNew(newSearchType); // Update searchTypeNew after setting searchResults
      } else {
        setSearchResults([]);
      }
    } catch (error) {
      console.error('Error performing search:', error);
    }
  };

  const isTrackInFavorites = (track) => {
    return favorites && favorites.some((fav) => fav.id === track.id);
  };

  const handleAddToFavorites = (track) => {
    setFavoritesState((prevFavorites) => {
      const newFavorites = Array.isArray(prevFavorites) ? [...prevFavorites] : [];
      newFavorites.push(track);
      localStorage.setItem('favorites', JSON.stringify(newFavorites)); // Save to localStorage
      return newFavorites;
    });

    setFavorites((prevFavorites) => {
      const newFavorites = Array.isArray(prevFavorites) ? [...prevFavorites] : [];
      newFavorites.push(track);
      return newFavorites;
    });
  };

  const handleRemoveFromFavorites = (track) => {
    setFavoritesState((prevFavorites) => {
      const newFavorites = Array.isArray(prevFavorites)
        ? prevFavorites.filter((fav) => fav.id !== track.id)
        : [];
      localStorage.setItem('favorites', JSON.stringify(newFavorites)); // Save to localStorage
      return newFavorites;
    });

    setFavorites((prevFavorites) => {
      return Array.isArray(prevFavorites)
        ? prevFavorites.filter((fav) => fav.id !== track.id)
        : [];
    });
  };

  return (
    <div>
      <h2>Search</h2>
      <div>
        <select value={searchTypeShow} onChange={(e) => setSearchTypeShow(e.target.value)} className='search-type-select'>
          <option value="track">Track</option>
          <option value="artist">Artist</option>
          <option value="album">Album</option>
          <option value="playlist">Playlist</option>
        </select>
        <input
          type="text"
          placeholder={`Enter ${searchTypeShow} name`}
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className='search-input'
        />
        <button onClick={handleSearch} className='search-button'>Search</button>
      </div>
      {searchResults && (
        <div>
          <h3>Search Results</h3>
          <ul className="top-artists-list">
            {searchTypeNew === "tracks" && searchResults.map((item) => (
              <li key={item.id} className="top-artists-item">
                <img
                  src={item.album.images[0]?.url}
                  alt={item.name}
                  className="album-art"
                />
                <div className="track-info">
                  <p className="track-name">{item.name}</p>
                  <p className="artists">
                    {item.artists.map((artist) => artist.name).join(', ')}
                  </p>
                  {isTrackInFavorites(item) ? (
                    <button
                      onClick={() => handleRemoveFromFavorites(item)}
                      className="remove-from-favorites-button"
                    >
                      Remove from Favorites
                    </button>
                  ) : (
                    <button
                      onClick={() => handleAddToFavorites(item)}
                      className="add-to-favorites-button"
                    >
                      Add to Favorites
                    </button>
                  )}
                </div>
              </li>
            ))}
            {searchTypeNew === "artists" && searchResults.map((item) => (
              <li key={item.id} className="top-artists-item">
                {item.images && item.images.length > 0 && (
                  <img
                    src={item.images[0]?.url} 
                    alt={item.name}
                    className="artist-image"
                  />
                )}
                <div className="artist-info">
                  <p className="artist-name">{item.name}</p>
                </div>
              </li>
            ))}
            {searchTypeNew === 'albums' && searchResults.map((item) =>( 
              <li key={item.id} className="top-artists-item">
                <img
                  src={item.images[0]?.url}
                  alt={item.name}
                  className="album-art"
                />
                <div className="track-info">
                  <p className="track-name">{item.name}</p>
                  <p className="artists">
                    {item.artists.map((artist) => artist.name).join(', ')}
                  </p>
                </div>
              </li>
            ))}
            {searchTypeNew === 'playlists' && searchResults.map((item) => (
                <li key={item.id} className="top-artists-item">
                <img
                  src={item.images[0]?.url}
                  alt={item.name}
                  className="album-art"
                />
                <div className="track-info">
                  <p className="track-name">{item.name}</p>
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Search;
