// App.js

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { getTokenFromUrl, setAccessToken, loginUrl } from './spotifyAuth';
import spotifyApi from './spotifyAuth';
import Login from './components/Login';
import TopArtists from './components/TopArtists';
import TopTracks from './components/TopTracks';
import TopGenresButton from './components/TopGenresButton';
import Slideshow from './components/Slideshow';
import './App.css';
import spotifyLogo from './Spotify Logo.png';

function App() {
  const [user, setUser] = useState(null);
  const [isSlideshowActive, setIsSlideshowActive] = useState(false);
  const [topTracks, setTopTracks] = useState([]);
  const [topArtists, setTopArtists] = useState([]);
  const [topGenres, setTopGenres] = useState([]);

  useEffect(() => {
    const hash = getTokenFromUrl();
    window.location.hash = '';
    const token = hash.access_token;

    if (token) {
      setAccessToken(token);

      // Fetch user data
      spotifyApi.getMe().then((userData) => {
        setUser(userData);
        setIsSlideshowActive(true);
      });

      // Fetch top tracks
      spotifyApi.getMyTopTracks({ limit: 5, time_range: 'short_term' }).then((response) => {
        setTopTracks(response.items);
      });

      // Fetch top artists
      spotifyApi.getMyTopArtists({ limit: 5, time_range: 'short_term' }).then((response) => {
        setTopArtists(response.items);
      });

      // Fetch top genres
      spotifyApi.getMyTopArtists({ limit: 5, time_range: 'short_term' }).then((response) => {
        const genres = response.items.reduce((acc, artist) => {
          return acc.concat(artist.genres);
        }, []);

        const genreCounts = genres.reduce((acc, genre) => {
          acc[genre] = (acc[genre] || 0) + 1;
          return acc;
        }, {});

        const sortedGenres = Object.keys(genreCounts).sort((a, b) => genreCounts[b] - genreCounts[a]);
        const top5Genres = sortedGenres.slice(0, 5);

        setTopGenres(top5Genres);
      });
    }
  }, []);

  const handleLogout = () => {
    setAccessToken(null);
    setUser(null);
    setIsSlideshowActive(false); // Stop the slideshow when logging out
  };

  return (
    <Router>
      <div className="App">
        <header className={`App-header ${isSlideshowActive ? 'hide-headline' : ''}`}>
        <Link to="/" className="home-link">
            <h1>Spotify Monthly Wrapped</h1>
          </Link>
          <div className="user-greeting">
            {user && <p>Hello {user.display_name || user.id}!</p>}
          </div>
          <Link to="/" className="home-link">
            {user && <h1>Show my Monthly Wrapped</h1>}
          </Link>
          <a href="https://www.spotify.com/" target="_blank" rel="noopener noreferrer">
            <img src={spotifyLogo} alt="Spotify Logo" className="spotify-logo" />
          </a>
          <nav>
            <ul>
              <li>
                {user ? (
                  <button onClick={handleLogout} className="logout-button">
                    Logout
                  </button>
                ) : (
                  <a href={loginUrl} className="login-button">
                    Login with Spotify
                  </a>
                )}
              </li>
            </ul>
          </nav>
        </header>

        {/* Routes */}
        <Routes>
          {user && isSlideshowActive && (
            <Route
              path="/"
              element={(
                <Slideshow
                  username={user.display_name || user.id}
                  topTracks={topTracks}
                  topArtists={topArtists}
                  topGenres={topGenres}
                />
              )}
            />
          )}
          <Route path="/login" element={<Login />} />
          {user && <Route path="/top-artists" element={<TopArtists setTopArtists={setTopArtists} />} />}
          {user && <Route path="/top-tracks" element={<TopTracks user={user} setTopTracks={setTopTracks} />} />}
          {user && <Route path="/top-genres" element={<TopGenresButton setTopGenres={setTopGenres} />} />}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
