// CurrentlyPlaying.js

import React, { useState, useEffect } from 'react';
import spotifyApi from '../spotifyAuth';

function CurrentlyPlaying({setFavorites}) {
  const [currentTrack, setCurrentTrack] = useState(null);

  useEffect(() => {
    // Fetch the user's currently playing track
    spotifyApi.getMyCurrentPlayingTrack().then((response) => {
      setCurrentTrack(response.item);
    });
  }, []);

  const handleAddToFavorites = (track) => {
    setFavorites((prevFavorites) => [...prevFavorites, track]);
  };

  return (
    <div>
      <h2>Currently Playing</h2>
      {currentTrack && (
        <div>
          <img
            src={currentTrack.album.images[0].url}
            alt={currentTrack.name}
            className="album-art-currently"
          />
          <div className="track-info">
            <p className="track-name">{currentTrack.name}</p>
            <p className="artists">{currentTrack.artists.map((artist) => artist.name).join(', ')}</p>
            <button onClick={() => handleAddToFavorites(currentTrack)} className="add-to-favorites-button">Add to Favorites</button>
          </div>
        </div>
      )}
      {!currentTrack && <p>No track currently playing</p>}
    </div>
  );
}

export default CurrentlyPlaying;
