import React, { useState, useEffect } from 'react';
import spotifyApi from '../spotifyAuth';

function TopArtists() {
  const [topArtists, setTopArtists] = useState([]);
  const [currentArtistIndex, setCurrentArtistIndex] = useState(0);

  useEffect(() => {
    // Fetch the user's top artists
    spotifyApi.getMyTopArtists({ limit: 20, time_range: 'short_term' }).then((response) => {
      setTopArtists(response.items);

      // Set up an interval to switch artists every 2 seconds
      const intervalId = setInterval(() => {
        console.log(currentArtistIndex);
        setCurrentArtistIndex((prevIndex) => (prevIndex + 1) % topArtists.length);
      }, 2000);

      // Clean up the interval when the component unmounts
      return () => clearInterval(intervalId);
    });
  }, []);

  return (
    <div>
      <h2>Top Artists This Month</h2>
      <div className="slideshow-container">
        {topArtists.map((artist, index) => (
          <div
            key={artist.id}
            className={`slide ${index === currentArtistIndex ? 'active' : ''}`}
          >
            {artist.images.length > 0 && (
              <img
                src={artist.images[0].url}
                alt={artist.name}
                className="artist-image"
              />
            )}
            <div className="artist-info">
              <p className="artist-name">{artist.name}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TopArtists;
