// components/TopArtists.js

import React, { useState, useEffect } from 'react';
import spotifyApi from '../spotifyAuth';

function TopArtists({ setSlideshow }) {
  const [topArtists, setTopArtists] = useState([]);
  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    // Fetch the user's top artists
    spotifyApi.getMyTopArtists({ limit: 5, time_range: 'short_term' }).then((response) => {
      setTopArtists(response.items);
      setSlideshow(true); // Activate the slideshow when top artists are fetched
    });
  }, [setSlideshow]);

  useEffect(() => {
    // Update the slideshow when topArtists changes
    const intervalId = setInterval(() => {
      setCurrentSlide((prevSlide) => (prevSlide + 1) % topArtists.length);
    }, 3000);

    // Clean up the interval when the component unmounts or topArtists changes
    return () => clearInterval(intervalId);
  }, [topArtists]);

  return (
    <div>
      <h2 className={topArtists.length > 0 ? '' : 'hide-headline'}>
        Top Artists This Month
      </h2>
      <div className="top-artists-container">
        {topArtists.map((artist, index) => (
          <div
            key={artist.id}
            className={`top-artists-item ${index === currentSlide ? 'active-slide' : ''}`}
          >
            <div className="artist-info">
              <p className="artist-name">{index + 1}. {artist.name}</p>
              <div className="artist-image">
                {artist.images.length > 0 && (
                  <img
                    src={artist.images[0].url}
                    alt={artist.name}
                    className="artist-image"
                  />
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TopArtists;
