import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import spotifyApi from '../spotifyAuth';

function FavoriteTracks({ user, setFavorites }) {
  const [favorites, setFavoritesState] = useState([]);
  const [newPlaylistName, setNewPlaylistName] = useState('');
  const [newPlaylistDescription, setNewPlaylistDescription] = useState('');

  useEffect(() => {
    // Load favorites from localStorage on component mount
    const storedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavoritesState(storedFavorites);
    setFavorites(storedFavorites);
  }, [setFavorites]);

  const handleRemoveFromFavorites = (track) => {
    setFavoritesState((prevFavorites) => {
      const newFavorites = Array.isArray(prevFavorites)
        ? prevFavorites.filter((fav) => fav.id !== track.id)
        : [];
      localStorage.setItem('favorites', JSON.stringify(newFavorites)); // Save to localStorage
      return newFavorites;
    });

    setFavorites((prevFavorites) => {
      return Array.isArray(prevFavorites)
        ? prevFavorites.filter((fav) => fav.id !== track.id)
        : [];
    });
  };

  const clearFavorites = () => {
    localStorage.setItem('favorites', JSON.stringify([])); // Initialize as an empty array
    setFavoritesState([]);
  };

  const createPlaylist = async () => {
    try {
      // Create a new playlist
      const response = await spotifyApi.createPlaylist(user.id, {
        name: newPlaylistName,
        public: false, // Set to true if you want the playlist to be public
        description: newPlaylistDescription,
      });

      const playlistId = response.id;

      // Get track URIs from favorites
      const trackUris = favorites.map((track) => track.uri);

      // Add tracks to the playlist
      await spotifyApi.addTracksToPlaylist(playlistId, trackUris);

      // Clear the input field
      setNewPlaylistName('');

      // Optionally, you can show a success message or navigate the user to the new playlist
      console.log('Playlist created successfully!');
    } catch (error) {
      console.error('Error creating playlist:', error);
    }
  };

  return (
    <div>
      <h2>Favorites</h2>
      {/* Input field for new playlist name */}
      <input
        type="text"
        placeholder="Enter playlist name"
        value={newPlaylistName}
        onChange={(e) => setNewPlaylistName(e.target.value)}
        className='search-input'
      />

      {/* Input field for new playlist name */}
      <input
        type="text"
        placeholder="Enter playlist description"
        value={newPlaylistDescription}
        onChange={(e) => setNewPlaylistDescription(e.target.value)}
        className='search-input'
      />
      
      {/* Button to create playlist */}
      <button onClick={createPlaylist} className='search-button'>
        Create Playlist
      </button>

      <div>
        <div>
      <button onClick={clearFavorites} className="remove-from-favorites-button">
        Clear Favorites
      </button>
      </div>
      </div>

      <ul className="favorites-list">
        {favorites.map((track) => (
          <li key={track.id} className="favorites-item">
            <img src={track.album.images[0].url} alt={track.name} className="album-art" />
            <div className="track-info">
              <p className="track-name">{track.name}</p>
              <p className="artists">{track.artists.map((artist) => artist.name).join(', ')}</p>
              <button onClick={() => handleRemoveFromFavorites(track)} className="remove-from-favorites-button">
                Remove from Favorites
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

FavoriteTracks.propTypes = {
  favorites: PropTypes.array.isRequired,
  removeFromFavorites: PropTypes.func.isRequired,
};

export default FavoriteTracks;
