// App.js

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { getTokenFromUrl, setAccessToken, loginUrl } from './spotifyAuth';
import spotifyApi from './spotifyAuth';
import Login from './components/Login';
import TopArtists from './components/TopArtists';
import TopTracks from './components/TopTracks';
import TopGenresButton from './components/TopGenresButton';
import Slideshow from './components/Slideshow';
import './App.css';
import spotifyLogo from './Spotify Logo.png';

function App() {
  const [user, setUser] = useState(null);
  const [isSlideshowActive, setIsSlideshowActive] = useState(false);

  useEffect(() => {
    const hash = getTokenFromUrl();
    window.location.hash = '';
    const token = hash.access_token;

    if (token) {
      setAccessToken(token);

      // Fetch user data
      spotifyApi.getMe().then((userData) => {
        setUser(userData);
        setIsSlideshowActive(true);
      });
    }    
  }, []);

  const handleLogout = () => {
    setAccessToken(null);
    setUser(null);
    setIsSlideshowActive(false); // Stop the slideshow when logging out
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <Link to="/" className="home-link">
            <h1>Spotify Monthly Wrapped</h1>
          </Link>
          <div className="user-greeting">
            {user && <p>Hello {user.display_name || user.id}!</p>}
          </div>
          <Link to="/" className="home-link">
            {user && <h1>Show my Monthly Wrapped</h1>}
          </Link>
          <a href="https://www.spotify.com/" target="_blank" rel="noopener noreferrer">
            <img src={spotifyLogo} alt="Spotify Logo" className="spotify-logo" />
          </a>
          <nav>
            <ul>
              <li>
                {user ? (
                  <button onClick={handleLogout} className="logout-button">
                    Logout
                  </button>
                ) : (
                  <a href={loginUrl} className="login-button">
                    Login with Spotify
                  </a>
                )}
              </li>
            </ul>
          </nav>
        </header>

        {/* Routes */}
        <Routes>
          <Route
            path="/"
            element={user && isSlideshowActive ? (
              <Slideshow>
                <TopTracks user={user} />
                <TopArtists setSlideshow={setIsSlideshowActive} />
                <TopGenresButton />
              </Slideshow>
            ) : null}
          />
          <Route path="/login" element={<Login />} />
          {user && <Route path="/top-artists" element={<TopArtists />} />}
          {user && <Route path="/top-tracks" element={<TopTracks user={user} />} />}
          {user && <Route path="/top-genres" element={<TopGenresButton />} />}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
