// components/FavoriteTracks/FavoriteTracks.js

import React from 'react';
import PropTypes from 'prop-types';

function FavoriteTracks({ favorites, removeFromFavorites }) {
  return (
    <div>
      <h2>Favorites</h2>
      <ul className="track-list">
        {favorites.map((track) => (
          <li key={track.id} className="track-item">
            <img src={track.album.images[2].url} alt={track.name} className="album-art" />
            <div className="track-info">
              <p className="track-name">{track.name}</p>
              <p className="artists">{track.artists.map((artist) => artist.name).join(', ')}</p>
              <button onClick={() => removeFromFavorites(track)} className="remove-from-favorites-button">
                Remove from Favorites
              </button>
            </div>
          </li>
        ))}
      </ul>
      {favorites.length > 0 && <button>Create Playlist</button>}
    </div>
  );
}

FavoriteTracks.propTypes = {
  favorites: PropTypes.array.isRequired,
  removeFromFavorites: PropTypes.func.isRequired,
};

export default FavoriteTracks;
