// components/Search.js
import React, { useState } from 'react';
import spotifyApi from '../spotifyAuth';

function Search() {
  const [query, setQuery] = useState('');
  const [searchType, setSearchType] = useState('track'); // Default to searching for tracks
  const [searchResults, setSearchResults] = useState(null);

  const handleSearch = async () => {
    try {
      // Use the Spotify Web API to search based on the selected type
      var searchTypeNew = '';
      if(searchType == 'track'){
        searchTypeNew = 'tracks';
      }
      else if(searchType == 'artist'){
        searchTypeNew = 'artists';
      }
      else if(searchType == 'playlist'){
        searchTypeNew = 'playlists';
      }
      else if(searchType == 'album'){
        searchTypeNew = 'albums';
      }
      const response = await spotifyApi.search(query, [searchType], { limit: 10 });

      // Extract and update state with the top results
      if (response[searchTypeNew] && response[searchTypeNew]['items']) {
        setSearchResults(response[searchTypeNew]['items']);
      } else {
        setSearchResults([]);
      }
    } catch (error) {
      console.error('Error performing search:', error);
    }
  };

  return (
    <div>
      <h2>Search</h2>
      <div>
        <select value={searchType} onChange={(e) => setSearchType(e.target.value)}>
          <option value="track">Track</option>
          <option value="artist">Artist</option>
          <option value="album">Album</option>
          <option value="playlist">Playlist</option>
        </select>
        <input
          type="text"
          placeholder={`Enter ${searchType} name`}
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {searchResults && (
        <div>
          <h3>Search Results</h3>
          <ul className="top-artists-list">
            {searchResults.tracks?.items.map((item) => (
              <li key={item.id} className="top-artists-item">
                <img
              src={item.album.images[2].url}
              alt={item.name}
              className="album-art"
            />
            <div className="track-info">
              <p className="track-name">{item.name}</p>
              <p className="artists">
                {item.artists.map((artist) => artist.name).join(', ')}
              </p>
              </div>
              </li>
            ))}
            {searchResults.artists?.items.map((item) => (
              <li key={item.id} className="top-artists-item">
                {item.images.length > 0 && (
                <img
                    src={item.images[2].url} 
                    alt={item.name}
                    className="artist-image"
                />
                )}
                <div className="artist-info">
                <p className="artist-name">{item.name}</p>
                </div>
                </li>
            ))}
            {searchResults.albums?.items.map((item) => (
              <li key={item.id} className="top-artists-item">{item.name} - Album</li>
            ))}
            {searchResults.playlists?.items.map((item) => (
              <li key={item.id} className="top-artists-item">{item.name} - Playlist</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Search;
