// components/RecentlyPlayed/RecentlyPlayed.js

import React, { useState, useEffect } from 'react';
import spotifyApi from '../spotifyAuth';
import PropTypes from 'prop-types';

function RecentlyPlayed({ setFavorites }) {
  const [recentlyPlayed, setRecentlyPlayed] = useState([]);
  const [favorites, setFavoritesState] = useState([]);
  const [showHeadline, setShowHeadline] = useState(false);

  useEffect(() => {
    // Load favorites from localStorage on component mount
    const storedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavoritesState(storedFavorites);
    setFavorites(storedFavorites);
  
    // Fetch the user's recently played tracks
    spotifyApi.getMyRecentlyPlayedTracks({ limit: 50 }).then((response) => {
      console.log('API response:', response);
      setRecentlyPlayed(response.items);
      setShowHeadline(true); // Set to true after fetching data
    });
  }, [setFavorites]);
  

  const isTrackInFavorites = (track) => {
    console.log('track:', track);
    console.log('favorites:', favorites);
  
    return (
      track &&
      track.track &&
      track.track.id &&
      favorites &&
      Array.isArray(favorites) &&
      favorites.some((fav) => {
        console.log('fav:', fav);
        return fav.id === track.track.id;
      })
    );
  };
  

  const handleAddToFavorites = (track) => {
    setFavoritesState((prevFavorites) => {
      const newFavorites = Array.isArray(prevFavorites) ? [...prevFavorites] : [];
      newFavorites.push(track.track);
      localStorage.setItem('favorites', JSON.stringify(newFavorites)); // Save to localStorage
      return newFavorites;
    });

    setFavorites((prevFavorites) => {
      const newFavorites = Array.isArray(prevFavorites) ? [...prevFavorites] : [];
      newFavorites.push(track.track);
      return newFavorites;
    });
  };

  const handleRemoveFromFavorites = (track) => {
    setFavoritesState((prevFavorites) => {
      const newFavorites = Array.isArray(prevFavorites)
        ? prevFavorites.filter((fav) => fav.id !== track.track.id)
        : [];
      localStorage.setItem('favorites', JSON.stringify(newFavorites)); // Save to localStorage
      return newFavorites;
    });

    setFavorites((prevFavorites) => {
      return Array.isArray(prevFavorites)
        ? prevFavorites.filter((fav) => fav.id !== track.track.id)
        : [];
    });
  };

  return (
    <div>
      {showHeadline && <h2>Recently Played</h2>}
      <ul className="recently-played-list">
        {recentlyPlayed.map((track) => (
          <li key={track.track.id} className="recently-played-item">
            <img
              src={track.track.album.images[2].url}
              alt={track.track.name}
              className="album-art"
            />
            <div className="track-info">
              <p className="track-name">{track.track.name}</p>
              <p className="artists">
                {track.track.artists.map((artist) => artist.name).join(', ')}
              </p>
              {isTrackInFavorites(track) ? (
                <button
                  onClick={() => handleRemoveFromFavorites(track)}
                  className="remove-from-favorites-button"
                >
                  Remove from Favorites
                </button>
              ) : (
                <button
                  onClick={() => handleAddToFavorites(track)}
                  className="add-to-favorites-button"
                >
                  Add to Favorites
                </button>
              )}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

RecentlyPlayed.propTypes = {
  setFavorites: PropTypes.func.isRequired,
};

export default RecentlyPlayed;
