import React, { useState, useEffect } from 'react';
import spotifyApi from '../spotifyAuth';

function Search() {
  const [query, setQuery] = useState('');
  const [searchType, setSearchType] = useState('track');
  const [searchTypeShow, setSearchTypeShow] = useState('track');
  const [searchTypeNew, setSearchTypeNew] = useState('tracks');
  const [searchResults, setSearchResults] = useState(null);

  const handleSearch = async () => {
    try {
      console.log(searchType);
      const response = await spotifyApi.search(query, [searchType], { limit: 10 });

      // Use the Spotify Web API to search based on the selected type
      setSearchType((prevSearchType) => {
        if (prevSearchType === 'track') {
          setSearchTypeNew('tracks');
        } else if (prevSearchType === 'artist') {
          setSearchTypeNew('artists');
        } else if (prevSearchType === 'playlist') {
          setSearchTypeNew('playlists');
        } else if (prevSearchType === 'album') {
          setSearchTypeNew('albums');
        }
        return prevSearchType; // Return the same value to update the state immediately
      });

      console.log(searchTypeNew);

      // Extract and update state with the top results
      if (response[searchTypeNew] && response[searchTypeNew]['items']) {
        setSearchResults(response[searchTypeNew]['items']);
      } else {
        setSearchResults([]);
      }
      console.log(searchTypeNew);
    } catch (error) {
      console.error('Error performing search:', error);
    }
  };

  return (
    <div>
      <h2>Search</h2>
      <div>
        <select value={searchTypeShow} onChange={(e) => setSearchTypeShow(e.target.value)}>
          <option value="track">Track</option>
          <option value="artist">Artist</option>
          <option value="album">Album</option>
          <option value="playlist">Playlist</option>
        </select>
        <input
          type="text"
          placeholder={`Enter ${searchType} name`}
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button onClick={() => {setSearchType(searchTypeShow), handleSearch()}}>Search</button>
      </div>
      {searchResults && (
        <div>
          <h3>Search Results</h3>
          <ul className="top-artists-list">
            {searchTypeNew === "tracks" && searchResults.map((item) => (
              <li key={item.id} className="top-artists-item">
                <img
                  src={item.album.images[2]?.url}
                  alt={item.name}
                  className="album-art"
                />
                <div className="track-info">
                  <p className="track-name">{item.name}</p>
                  <p className="artists">
                    {item.artists.map((artist) => artist.name).join(', ')}
                  </p>
                </div>
              </li>
            ))}
            {searchTypeNew === "artists" && searchResults.map((item) => (
              <li key={item.id} className="top-artists-item">
                {item.images && item.images.length > 0 && (
                  <img
                    src={item.images[2]?.url} 
                    alt={item.name}
                    className="artist-image"
                  />
                )}
                <div className="artist-info">
                  <p className="artist-name">{item.name}</p>
                </div>
              </li>
            ))}
            {searchTypeNew === 'albums' &&
              searchResults.map((item) => <li key={item.id} className="top-artists-item">{item.name}</li>)}
            {searchTypeNew === 'playlists' &&
              searchResults.map((item) => (
                <li key={item.id} className="top-artists-item">
                  {item.name}
                </li>
              ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Search;
