// App.js

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { getTokenFromUrl, setAccessToken, loginUrl } from './spotifyAuth';
import spotifyApi from './spotifyAuth';
import Login from './components/Login';
import RecentlyPlayed from './components/RecentlyPlayed';
import TopArtists from './components/TopArtists';
import TopTracks from './components/TopTracks';
import './App.css';
import spotifyLogo from './Spotify Logo.png';
import CurrentlyPlaying from './components/CurrentlyPlaying';

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const hash = getTokenFromUrl();
    window.location.hash = '';
    const token = hash.access_token;

    if (token) {
      setAccessToken(token);

      // Fetch user data
      spotifyApi.getMe().then((userData) => {
        setUser(userData);
      });
    }
  }, []);

  const handleLogout = () => {
    // Implement logout logic
    // For example, you can clear the access token and user data
    setAccessToken(null);
    setUser(null);
  };

  const clearFavorites = () => {
    setFavorites([]); // Clear the favorites when the "Favorites" button is clicked
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <Link to="/" className="home-link">
            <h1>Spotify Monthly Wrapped</h1>
          </Link>
          <div className="user-greeting">
              {user && <p>Hello {user.display_name || user.id}!</p>}
          </div>
          <img src={spotifyLogo} alt="Spotify Logo" className="spotify-logo" />
          <nav>
            <ul>
              <li>
                {user ? (
                  <button onClick={handleLogout} className="logout-button">
                    Logout
                  </button>
                ) : (
                  <a href={loginUrl} className="login-button">
                    Login with Spotify
                  </a>
                )}
              </li>
              {user && (
                <li>
                  <Link to="/recently-played" className="nav-link">
                    Recently Played Tracks
                  </Link>
                </li>
              )}
              {user && (
                <li>
                  <Link to="/top-tracks" className="nav-link">
                    Top Tracks This Month
                  </Link>
                </li>
              )}
              {user && (
                <li>
                  <Link to="/top-artists" className="nav-link">
                    Top Artists This Month
                  </Link>
                </li>
              )}
              {user && (
                <li>
                  <Link to="/currently-playing" className="nav-link">
                    Currently Playing
                  </Link>
                </li>
              )}
              <Link to="/favorites" className="nav-link">
                      Favorites
                    </Link>
              {/* Add more navigation links for other features */}
            </ul>
          </nav>
        </header>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          {user && <Route path="/recently-played" element={<RecentlyPlayed />} />}
          {user && <Route path="/top-artists" element={<TopArtists />} />}
          {user && <Route path="/top-tracks" element={<TopTracks />} />}
          {user && <Route path="/currently-playing" element={<CurrentlyPlaying />} />}
          <Route path="/favorites" element={<Favorites favorites={favorites} />} />
          {/* Add more routes for other features */}
        </Routes>
      </div>
    </Router>
  );
}

function Favorites({ favorites }) {
  return (
    <div>
      <h2>Favorites</h2>
      <ul>
        {favorites.map((fav) => (
          <li key={fav.id}>{fav.name}</li>
        ))}
      </ul>
      {favorites.length > 0 && <button>Create Playlist</button>}
    </div>
  );
}

// New component for the home page
function Home() {
  return (
    <div>
      {/* Add content for the home page */}
    </div>
  );
}

export default App;
