// App.js

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { getTokenFromUrl, setAccessToken, loginUrl } from './spotifyAuth';
import spotifyApi from './spotifyAuth';
import Login from './components/Login';
import RecentlyPlayed from './components/RecentlyPlayed';
import './App.css';

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const hash = getTokenFromUrl();
    window.location.hash = '';
    const token = hash.access_token;

    if (token) {
      setAccessToken(token);

      // Fetch user data
      spotifyApi.getMe().then((userData) => {
        setUser(userData);
      });
    }
  }, []);

  const handleLogout = () => {
    // Implement logout logic
    // For example, you can clear the access token and user data
    setAccessToken(null);
    setUser(null);
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <div className="header-content">
            <Link to="/" className="home-link">
              <h1>Spotify App</h1>
            </Link>
            <nav>
              <ul>
                <li>
                  {user ? (
                    <button onClick={handleLogout} className="logout-button">
                      Logout
                    </button>
                  ) : (
                    <a href={loginUrl} className="login-button">
                      Login with Spotify
                    </a>
                  )}
                </li>
                {user && (
                  <li className="user-greeting">
                    <p>Hello {user.display_name || user.id}!</p>
                  </li>
                )}
                {user && (
                  <li>
                    <Link to="/recently-played" className="nav-link">
                      Recently Played
                    </Link>
                  </li>
                )}
                {/* Add more navigation links for other features */}
              </ul>
            </nav>
          </div>
        </header>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          {user && <Route path="/recently-played" element={<RecentlyPlayed />} />}
          {/* Add more routes for other features */}
        </Routes>
      </div>
    </Router>
  );
}

// New component for the home page
function Home() {
  return (
    <div>
      {/* Add content for the home page */}
    </div>
  );
}

export default App;
