// components/Search.js
import React, { useState } from 'react';
import spotifyApi from '../spotifyAuth';

function Search() {
  const [query, setQuery] = useState('');
  const [searchResults, setSearchResults] = useState(null);

  const handleSearch = async () => {
    try {
      // Use the Spotify Web API to search for tracks, artists, albums, and playlists
      const response = await spotifyApi.search(query, ['track', 'artist', 'album', 'playlist'], { limit: 10 });

      // Extract top results from each type
      const topResults = [];
      if (response.tracks?.items.length > 0) {
        topResults.push(...response.tracks.items.slice(0, 3));
      }
      if (response.artists?.items.length > 0) {
        topResults.push(...response.artists.items.slice(0, 3));
      }
      if (response.albums?.items.length > 0) {
        topResults.push(...response.albums.items.slice(0, 3));
      }
      if (response.playlists?.items.length > 0) {
        topResults.push(...response.playlists.items.slice(0, 3));
      }

      // Update state with the top results
      setSearchResults(topResults);
    } catch (error) {
      console.error('Error performing search:', error);
    }
  };

  return (
    <div>
      <h2>Search</h2>
      <div>
        <input
          type="text"
          placeholder="Enter search query"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {searchResults && searchResults.length > 0 && (
        <div>
          <h3>Top Results</h3>
          <ul>
            {searchResults.map((item) => (
              <li key={item.id}>{item.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Search;
