// components/TopArtists.js

import React, { useState, useEffect } from 'react';
import spotifyApi from '../spotifyAuth';

function TopArtists() {
  const [topArtists, setTopArtists] = useState([]);
  const [currentArtistIndex, setCurrentArtistIndex] = useState(0);

  useEffect(() => {
    // Fetch the user's top artists
    spotifyApi.getMyTopArtists({ limit: 20, time_range: 'short_term' }).then((response) => {
      setTopArtists(response.items);
    });
  }, []);

  useEffect(() => {
    let interval;

    const startSlideshow = () => {
      interval = setInterval(() => {
        // Update the current artist index for the continuous slide effect
        setCurrentArtistIndex((prevIndex) =>
          prevIndex === topArtists.length - 1 ? prevIndex + 1 : 0
        );
      }, 2000); // Change slide every 2 seconds
    };

    startSlideshow();

    return () => {
      clearInterval(interval); // Cleanup the interval on component unmount
    };
  }, [topArtists]);

  return (
    <div className="top-artists-container">
      <h2>Top Artists This Month</h2>
      <div className="artist-slideshow">
        {topArtists.map((artist, index) => (
          <div
            key={artist.id}
            className={`artist-slide ${index === currentArtistIndex ? 'active' : ''}`}
            style={{
              backgroundImage: `url(${artist.images.length > 0 ? artist.images[0].url : ''})`,
            }}
          >
            <div className="overlay">
              <div className="artist-info">
                <p className="artist-name">{artist.name}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TopArtists;
