// components/TopArtists.js

import React, { useState, useEffect } from 'react';
import spotifyApi from '../spotifyAuth';

function TopArtists() {
  const [topArtists, setTopArtists] = useState([]);
  const [showHeadline, setShowHeadline] = useState(false);

  useEffect(() => {
    // Fetch the user's top artists
    spotifyApi.getMyTopArtists({ limit: 20, time_range: 'short_term' }).then((response) => {
      setTopArtists(response.items);
      setShowHeadline(true); // Set to true after fetching data
    });
  }, []);

  return (
    <div className="story-container">
      {showHeadline && <h2 className="story-text">Top Artists This Month</h2>}
      <div className="story-content">
        {topArtists.map((artist, index) => (
          <div key={artist.id} className={`story-image artist-${index}`}>
            {artist.images.length > 0 && (
              <img
                src={artist.images[0].url} // Assuming you want to display the artist image
                alt={artist.name}
                className="artist-image"
              />
            )}
            <div className="overlay"></div>
            <div className="story-text artist-name">{artist.name}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TopArtists;
