// components/TopArtists.js

import React, { useState, useEffect } from 'react';
import spotifyApi from '../spotifyAuth';

function TopArtists() {
  const [topArtists, setTopArtists] = useState([]);
  const [currentArtistIndex, setCurrentArtistIndex] = useState(0);  

  useEffect(() => {
    // Fetch the user's top artists
    spotifyApi.getMyTopArtists({ limit: 20, time_range: 'short_term' }).then((response) => {
      setTopArtists(response.items);
    });
  }, []);    

  return (
    <div className="top-artists-container">
      <h2>Top Artists This Month</h2>
      <div className="slideshow">
        {topArtists.map((artist, index) => (
          <div
            key={artist.id}
            className={`artist-slide`}
            style={{
              backgroundImage: `url(${artist.images.length > 0 ? artist.images[0].url : ''})`,
            }}
          >
            <div className="overlay">
              <div className="artist-info">
                <p className="artist-name">{index+1}{'. '}{artist.name}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TopArtists;
