// components/FavoriteTracks.js

import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';

function FavoriteTracks({ setFavorites }) {

  const [favorites, setFavoritesState] = useState([]);

  useEffect(() => {
    // Load favorites from localStorage on component mount
    const storedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavoritesState(storedFavorites);
    setFavorites(storedFavorites);
    });

  const handleRemoveFromFavorites = (track) => {
    setFavoritesState((prevFavorites) => {
      const newFavorites = Array.isArray(prevFavorites)
        ? prevFavorites.filter((fav) => fav.id !== track.id)
        : [];
      localStorage.setItem('favorites', JSON.stringify(newFavorites)); // Save to localStorage
      return newFavorites;
    });

    setFavorites((prevFavorites) => {
      return Array.isArray(prevFavorites)
        ? prevFavorites.filter((fav) => fav.id !== track.id)
        : [];
    });
  };

  const clearFavorites = () => {
    localStorage.removeItem('favorites'); // Remove the 'favorites' key from localStorage
    setFavorites([]); // Update state to an empty array
    setFavoritesState([]); // Update local state to an empty array
  };

  return (
    <div>
      <h2>Favorites</h2>
      <button onClick={clearFavorites} className="remove-from-favorites-button">Clear Favorites</button>
      <ul className="favorites-list">
        {favorites.map((track) => (
          <li key={track.id} className="favorites-item">
            <img src={track.album.images[0].url} alt={track.name} className="album-art" />
            <div className="track-info">
              <p className="track-name">{track.name}</p>
              <p className="artists">{track.artists.map((artist) => artist.name).join(', ')}</p>
              <button onClick={() => handleRemoveFromFavorites(track)} className="remove-from-favorites-button">
                Remove from Favorites
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

FavoriteTracks.propTypes = {
  favorites: PropTypes.array.isRequired,
  removeFromFavorites: PropTypes.func.isRequired,
};

export default FavoriteTracks;
