// components/TopTracks/TopTracks.js

import React, { useState, useEffect } from 'react';
import spotifyApi from '../spotifyAuth';
import PropTypes from 'prop-types';

function TopTracks({ user, setFavorites }) {
  const [topTracks, setTopTracks] = useState([]);
  const [favorites, setFavoritesState] = useState([]);
  const [showHeadline, setShowHeadline] = useState(false);

  const [newPlaylistId, setNewPlaylistId] = useState(null);

  useEffect(() => {
    // Load favorites from localStorage on component mount
    const storedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setFavoritesState(storedFavorites);
    setFavorites(storedFavorites);

    // Fetch the user's top tracks
    spotifyApi.getMyTopTracks({ limit: 50, time_range: 'short_term' }).then((response) => {
      setTopTracks(response.items);
      setShowHeadline(true); // Set to true after fetching data
    });
  }, [user, setFavorites]);

  const isTrackInFavorites = (track) => {
    return favorites && favorites.some((fav) => fav.id === track.id);
  };

  const handleAddToFavorites = (track) => {
    setFavoritesState((prevFavorites) => {
      const newFavorites = Array.isArray(prevFavorites) ? [...prevFavorites] : [];
      newFavorites.push(track);
      localStorage.setItem('favorites', JSON.stringify(newFavorites)); // Save to localStorage
      return newFavorites;
    });

    setFavorites((prevFavorites) => {
      const newFavorites = Array.isArray(prevFavorites) ? [...prevFavorites] : [];
      newFavorites.push(track);
      return newFavorites;
    });
  };

  const handleRemoveFromFavorites = (track) => {
    setFavoritesState((prevFavorites) => {
      const newFavorites = Array.isArray(prevFavorites)
        ? prevFavorites.filter((fav) => fav.id !== track.id)
        : [];
      localStorage.setItem('favorites', JSON.stringify(newFavorites)); // Save to localStorage
      return newFavorites;
    });

    setFavorites((prevFavorites) => {
      return Array.isArray(prevFavorites)
        ? prevFavorites.filter((fav) => fav.id !== track.id)
        : [];
    });
  };

  const createPlaylist = async () => {
    // Implement logic to create a playlist with top tracks
    // Use the user.id, topTracks, and Spotify API
    // Example:
    spotifyApi.createPlaylist(user.id, {
      name: 'Top Tracks Playlist',
      public: false, // Set to true if you want the playlist to be public
      description: 'A playlist of your top tracks this month',
    }).then((playlist) => {
      const trackUris = topTracks.map((track) => track.uri);
      spotifyApi.addTracksToPlaylist(playlist.id, trackUris).then(() => {
        // Playlist created successfully
        console.log('Playlist created successfully!');
      });
    });
  };

  return (
    <div>
      {showHeadline && <h2>Top Tracks</h2>}
      <button onClick={createPlaylist}>Create Playlist</button>
      <ul className="top-tracks-list">
        {topTracks.map((track) => (
          <li key={track.id} className="top-tracks-item">
            {track.album.images.length > 0 && (
              <img
                src={track.album.images[2].url} // Assuming you want to display the album art
                alt={track.name}
                className="track-image"
              />
            )}
            <div className="track-info">
              <p className="track-name">{track.name}</p>
              <p className="artists">{track.artists.map((artist) => artist.name).join(', ')}</p>
              {isTrackInFavorites(track) ? (
                <button
                  onClick={() => handleRemoveFromFavorites(track)}
                  className="remove-from-favorites-button"
                >
                  Remove from Favorites
                </button>
              ) : (
                <button
                  onClick={() => handleAddToFavorites(track)}
                  className="add-to-favorites-button"
                >
                  Add to Favorites
                </button>
              )}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

TopTracks.propTypes = {
  setFavorites: PropTypes.func.isRequired,
};

export default TopTracks;
